mapper.select:
SELECT *
FROM(SELECT ROWNUM RN, A.*
FROM(SELECT B.*, M.* FROM BOARD B, MEMBER M

	 AND M_NICKNAME LIKE '%' || #{m_nickname} || '%' 
	 AND B_SUBJECT LIKE '%' || #{b_subject} || '%' 
	 AND B_TITLE LIKE '%' || #{b_title} || '%' 
	 AND TO_CHAR(B_WDATE, 'YYYY-MM-DD') = #{b_wdate} 
	AND B.B_EMAIL = M.M_EMAIL

)A
)
WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
   		UPDATE BOARD
   		SET B_TITLE = #{b_title}, B_CONTENT = #{b_content}, B_SUBJECT = #{b_subject}, B_ANONY = #{b_anony}
		WHERE B_NO = #{b_no}
