mapper.select:
SELECT COUNT(*) FROM COUNSELOR
AND C_NAME LIKE '%'||#{c_name}||'%' AND C_GENDER LIKE '%'||#{c_gender}||'%' AND C_GRADE LIKE '%'||#{c_grade}||'%' AND TO_CHAR(C_BIRTHDATE, 'YYYY-MM-DD') = #{c_birthdate}
AND C_EMAIL LIKE '%'||#{c_email}||'%' AND C_PHONE LIKE '%'||#{c_phone}||'%' AND C_ADDRESS LIKE '%'||#{c_address}||'%' AND C_STATUS LIKE '%'||#{c_status}||'%'

SELECT * FROM (SELECT ROWNUM RN, A.* FROM (SELECT * FROM COUNSELOR
AND C_NAME LIKE '%'||#{c_name}||'%' AND C_GENDER LIKE '%'||#{c_gender}||'%' AND C_GRADE LIKE '%'||#{c_grade}||'%' AND TO_CHAR(C_BIRTHDATE, 'YYYY-MM-DD') = #{c_birthdate}
AND C_EMAIL LIKE '%'||#{c_email}||'%' AND C_PHONE LIKE '%'||#{c_phone}||'%' AND C_ADDRESS LIKE '%'||#{c_address}||'%' AND C_STATUS LIKE '%'||#{c_status}||'%'
) A ) WHERE RN BETWEEN #{start} AND #{end}

select * from counselor
where c_email = #{c_email}
and
c_password = #{c_password}

mapper.update:
UPDATE COUNSELOR SET
C_PICTURE=#{c_picture},
C_PICTUREPATH=#{c_picturepath}
WHERE C_EMAIL=#{c_email}

mapper.select:
SELECT cd_time FROM cs_date
where c_email=#{c_email}
and
cd_day = #{cd_day}

SELECT PR_TIME FROM PERSONAL_RESERVE
WHERE C_EMAIL =
#{c_email}
AND PR_DATE = #{pr_date}

mapper.update:
UPDATE CS_UPGRADEAPPLY SET
CU_STATUS=#{cu_status}
, CU_REJECTION=#{cu_rejection}
WHERE CU_NO=#{cu_no}

UPDATE COUNSELOR SET
C_GRADE=#{c_grade}
WHERE
C_EMAIL=#{c_email}

mapper.select:
SELECT * FROM(SELECT ROWNUM RN, A.* FROM(SELECT C.*, P.P_KAKAO, P.P_KAKAOPRICE, P.P_ZOOM, P.P_ZOOMPRICE, P.P_PHONE, P.P_PHONEPRICE FROM COUNSELOR C, PRICE P
AND c_name LIKE '%' || #{c_name} || '%' AND c_type1 LIKE '%' || #{c_type1} || '%' AND c_type2 LIKE '%' || #{c_type2} || '%' AND c_type3 LIKE '%' || #{c_type3} || '%' AND c_type4 LIKE '%' || #{c_type4} || '%' AND c_type5 LIKE '%' || #{c_type5} || '%' AND c_type6 LIKE '%' || #{c_type6} || '%' AND p_kakao LIKE '%' || #{p_kakao} || '%' AND p_zoom LIKE '%' || #{p_zoom} || '%' AND p_phone LIKE '%' || #{p_phone} || '%' AND c_gender LIKE '%' || #{c_gender} || '%' and c.c_email = p.c_email
)A ) WHERE RN BETWEEN #{start} AND #{end}

select * from counselor
where c_email = #{c_email}
and
c_phone = #{c_phone}

mapper.update:
update counselor set c_password = #{c_password}
where
c_email = #{c_email}
and c_phone = #{c_phone}
