mapper.update:
UPDATE FAQ 
SET F_TITLE = #{f_title}, F_SUBJECT = #{f_subject}
WHERE F_NO = #{f_no}

mapper.select:
SELECT *
FROM (SELECT ROWNUM RN, A.*
FROM (SELECT *
FROM FAQ 
	 AND C_NAME LIKE '%' || #{c_name} || '%' 
 AND F_TITLE LIKE '%' || #{f_title} || '%' 	

) A
)
WHERE RN BETWEEN #{start} AND #{end}
