mapper.select:
SELECT * 
FROM (
      SELECT ROWNUM RN, A.* 
      FROM (SELECT * FROM group_counsel 
      ORDER BY gc_no DESC

   ) A
)
WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
update group_reserve
set  gr_status = 1
where gr_no = #{gr_no}

mapper.select:
select gr_subject
from group_reserve
where gr_no = #{gr_no}

mapper.update:
update group_counsel
set gc_report = #{gc_report}, gc_result = #{gc_result}
where gc_no = #{gc_no};
