mapper.update:
UPDATE GROUP_COUNSEL
GC_DATE = #{gc_date}, GC_STARTDATE = #{gc_startdate}, GC_FINALDATE = #{gc_finaldate}, GC_TITLE = #{gc_title}, GC_TYPE = #{gc_type}, GC_MIN_PERSON = #{gc_min_person}, GC_MAX_PERSON = #{gc_max_person}, GC_PRICE = #{gc_price}, GC_TIME = #{gc_time}, GC_KAKAO = #{gc_kakao}, GC_SUMNAIL = #{gc_sumnail}, GC_INFOS = #{gc_infos}
WHERE GC_NO = #{gc_no}

mapper.select:
SELECT *
FROM (
SELECT ROWNUM RN, A.*
FROM (SELECT * FROM
group_counsel
ORDER BY gc_no DESC
) A
)
WHERE RN BETWEEN #{start} AND
#{end}

SELECT GR_SUBJECT
FROM GROUP_RESERVE
WHERE GR_NO = #{gr_no}

mapper.update:
UPDATE GROUP_COUNSEL
SET GC_REPORT = #{gc_report},
GC_RESULT = #{gc_result}
WHERE GC_NO = #{gc_no}

mapper.select:
SELECT *
FROM (SELECT ROWNUM RN, A.*
FROM (SELECT *
FROM GROUP_COUNSEL

	 AND TO_CHAR(GC_DATE, 'YYYY-MM-DD') = #{gc_date} 
	AND gc_type like '%'||#{gc_type}||'%'
	AND gc_status like '%'||#{gc_status}||'%'
	AND gc_title like '%'||#{gc_title}||'%'

) A
)
WHERE RN BETWEEN #{start} AND #{end}

SELECT * FROM (SELECT ROWNUM RN, A.*
FROM (SELECT GR.*, M.M_NICKNAME
FROM
GROUP_RESERVE GR, MEMBER M

	 AND TO_CHAR(gr_reservedate, 'YYYY-MM-DD') =
		#{gr_reservedate} 
	AND gr_status like '%'||#{gr_status}||'%'
	AND m_email like '%'||#{m_email}||'%'
	AND m_nickname like '%'||#{m_nickname}||'%'
	AND M.M_EMAIL = GR.M_EMAIL
	AND GC_NO = #{gc_no}

) A
)
WHERE RN BETWEEN
#{start} AND #{end}

mapper.update:
UPDATE GROUP_COUNSEL SET
GC_STATUS = '2'
WHERE GC_NO = ${gc_no}

UPDATE GROUP_COUNSEL
SET GC_PERSON = GC_PERSON +1
WHERE GC_NO = #{gc_no}
