mapper.update:
update member set m_nickname = #{m_nickname} 
where m_email = #{m_email}
and	m_type = #{m_type}

mapper.select:
select *
from member
where m_password = #{m_password} 
and m_email = #{m_email} 
and m_type = #{m_type}

