mapper.select:
SELECT *
FROM (SELECT ROWNUM RN, A.*
FROM (SELECT *
FROM THERAPY

	AND t_name like '%'||#{t_name}||'%'
	AND t_title like '%'||#{t_title}||'%'

) A
)
WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
UPDATE THERAPY
SET t_picture = #{t_picture}, t_title = #{t_title}, t_subject =
#{t_subject}
WHERE t_no = #{t_no}
