mapper.select:
	  SELECT *
      FROM (
      SELECT ROWNUM RN, A.*
      FROM (SELECT * FROM short_comment
      ORDER BY sc_no DESC

      ) A
      )
      WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
update short_comment
set sc_title = #{sc_title}, sc_subject = #{sc_subject}
where sc_no = #{sc_no}
